#!/usr/bin/env python3
"""Generate 300+ prompt templates for Prompt Library."""
import json

prompts = []
pid = 1

def add(name, cat, content):
    global pid
    prompts.append({"id": str(pid), "name": name, "category": cat, "content": content})
    pid += 1

